import { useState } from "react";
import TextField from "@mui/material/TextField";
import { Box, Button, Step, Stepper } from "@mui/material";
import MyDropzone from "../dropzone/DropZone";
import PostsService from "../../src/services/post.service";
import Autocomplete from "../autocomplete/Autocomplete";

const PostForm = () => {
  const [newPost, setNewPost] = useState({});
  const [success, setSuccess] = useState(false);

  const handleChange = (e) => {
    setNewPost({ ...newPost, [e.target.name]: e.target.value });
    console.log(newPost);
  };
  const handleClick = (e) => {
    e.preventDefault();
    PostsService.createPost(newPost);
    setSuccess(true);
    setNewPost({});
  };

  return (
    <div>
      <Stepper activeStep={1} alternativeLabel>
        {steps.map((label) => (
          <Step key={label}>
            <StepLabel>{label}</StepLabel>
          </Step>
        ))}
      </Stepper>
      <Box
        component="form"
        sx={{
          display: "flex",
          flexDirection: "column",
          ".MuiInputBase-root, .MuiButton-root": {
            mb: 2,
          },
        }}
      >
        <TextField
          label="Outlined"
          variant="outlined"
          onChange={handleChange}
          value={newPost.title || ""}
          type="text"
          name="title"
          id="title"
        />
        <TextField
          label="Outlined"
          variant="outlined"
          multiline
          rows={4}
          onChange={handleChange}
          value={newPost.content || ""}
          type="text"
          name="content"
          id="content"
        />
        <Autocomplete newPost={newPost} setNewPost={setNewPost} />
        <MyDropzone newPost={newPost} setNewPost={setNewPost} />

        <Button variant="contained" onClick={handleClick}>
          Submit
        </Button>
        {success && <p style={{ color: "green" }}>Post created</p>}
      </Box>
    </div>
  );
};

export default PostForm;
