import { Box, Button } from "@mui/material";
import { useState } from "react";
import { useEffect } from "react";
import { useNavigate } from "react-router-dom";
import PostsService from "../../src/services/post.service";

const PostList = () => {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAllPosts();
  }, []);

  const getAllPosts = async () => {
    try {
      const allPosts = await PostsService.getPosts();
      setPosts(allPosts);
      console.log(allPosts);
    } catch (error) {
      console.log(error);
    }
  };

  const handleDelete = (e, id) => {
    e.preventDefault();
    fetch(`http://localhost:8000/api/posts/${id}`, {
      method: "DELETE",
    })
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        const filteredPosts = posts.filter((post) => post._id !== id);
        setPosts(filteredPosts);
      });
  };

  const handleClick = (e, id) => {
    navigate(`/posts/${id}`);
  };

  return (
    <Box>
      {posts?.map((post) => (
        <div key={post.id}>
          {post._id && post.uploadFiles.length > 0 && (
            <img
              style={{ width: "150px" }}
              src={post.uploadFiles[0]?.Location}
              alt=""
            />
          )}
          <h2>{post.title}</h2>
          <p>{post.content}</p>
          <Button
            variant="outlined"
            sx={{ mb: 4 }}
            onClick={(e) => handleClick(e, post._id)}
          >
            voir
          </Button>
          <Button
            variant="outlined"
            sx={{ mb: 4 }}
            onClick={(e) => handleDelete(e, post._id)}
          >
            Delete
          </Button>
        </div>
      ))}
    </Box>
  );
};

export default PostList;
