import { TextField } from "@mui/material";
import React, { useRef, useEffect, useState } from "react";

function Autocomplete({ id }) {
  const inputRef = useRef(null);
  const [autocomplete, setAutocomplete] = useState(null);

  useEffect(() => {
    if (inputRef.current) {
      setAutocomplete = new window.google.maps.places.Autocomplete(
        inputRef.current,
        {
          types: ["geocode"],
        }
      );
      autocomplete.addListener("place_changed", onPlaceChanged);
    }
  }, [inputRef]);

  function onPlaceChanged() {
    const place = autocomplete.getPlace();

    if (!place.geometry) {
      console.error("Invalid place geometry");
      return;
    }

    // Extraire des informations sur l'emplacement sélectionné
    const address = place.formatted_address;
    const latitude = place.geometry.location.lat();
    const longitude = place.geometry.location.lng();
    const placeId = place.place_id;

    // Utiliser les informations pour mettre à jour l'état de votre application
    // ou appeler une fonction de rappel fournie par les props
    // Par exemple : onPlaceSelected(address, latitude, longitude, placeId);
  }

  const handleChange = (e) => {
    console.log(e.target.value);
  };

  return (
    // <input id={id} ref={inputRef} placeholder="Start typing your address..." />
    <TextField
      label="Start typing your address..."
      variant="outlined"
      type="text"
      name="title"
      onChange={handleChange}
      id={id}
      inputRef={inputRef}
    />
  );
}

export default Autocomplete;
