import { Box, Grid, Paper } from "@mui/material";
import { useEffect } from "react";
import { useDropzone } from "react-dropzone";

function MyDropzone({ newPost, setNewPost }) {
  const { acceptedFiles, getRootProps, getInputProps } = useDropzone({
    accept: "image/*",
  });

  const files = acceptedFiles.map((file) => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  useEffect(() => {
    setNewPost({ ...newPost, uploadFiles: acceptedFiles });
    console.log(newPost);
  }, [acceptedFiles]);

  return (
    <div {...getRootProps()}>
      <input {...getInputProps()} />
      {[...acceptedFiles].map((file) => (
        <img
          style={{ width: "300px", height: "300px" }}
          src={URL.createObjectURL(file)}
          alt=""
        />
      ))}
      <p>Déposez des fichiers ici, ou cliquez pour sélectionner des fichiers</p>
      <ul>{files}</ul>
      <Box sx={{ flexGrow: 1 }}>
        <Grid container spacing={2} {...getRootProps()}>
          {[...Array(8)].map((_, index) => (
            <Grid item xs={3} key={index}>
              <Paper
                sx={{
                  height: 100,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center",
                  border: "1px solid gray",
                  cursor: "pointer",
                }}
                {...getInputProps()}
              >
                <span>
                  Déposez des fichiers ici, ou cliquez pour sélectionner des
                  fichiers
                </span>
              </Paper>
            </Grid>
          ))}
        </Grid>
        <ul>{files}</ul>
      </Box>
    </div>
  );
}

export default MyDropzone;
