import { useEffect, useState } from "react";
import { useDropzone } from "react-dropzone";

import Grid from "@mui/material/Grid";
import Paper from "@mui/material/Paper";
import Box from "@mui/material/Box";
import { IconButton } from "@mui/material";
import DeleteIcon from "@mui/icons-material/Delete";

function MyDropzone({ newPost, setNewPost }) {
  const [imageURLs, setImageURLs] = useState(Array(8).fill(""));

  const { acceptedFiles, getRootProps, getInputProps, isDragActive } =
    useDropzone({
      accept: "image/*",
      onDrop: (acceptedFiles) => {
        const newImageURLs = [...imageURLs];
        acceptedFiles.map((file, index) => {
          const reader = new FileReader();
          reader.readAsDataURL(file);
          reader.onload = () => {
            newImageURLs[index] = reader.result;
            setImageURLs(newImageURLs);
          };
        });
      },
    });

  const files = acceptedFiles.map((file) => (
    <li key={file.path}>
      {file.path} - {file.size} bytes
    </li>
  ));

  const deleteImage = (index, e) => {
    e.stopPropagation();
    setFiles(files.filter((file, i) => i !== index));
    const newImageURLs = [...imageURLs];
    newImageURLs[index] = "";
    setImageURLs(newImageURLs);
  };

  useEffect(() => {
    setNewPost({ ...newPost, uploadFiles: acceptedFiles });
    console.log(newPost);
  }, [acceptedFiles]);

  return (
    <Box sx={{ flexGrow: 1 }}>
      <Grid container spacing={2} {...getRootProps()}>
        {[...Array(8)].map((_, index) => (
          <Grid item xs={3} key={index}>
            <Paper
              className={`dropzone-paper${isDragActive ? " active" : ""}`}
              sx={{
                height: 100,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                border: "2px dashed gray",
                cursor: "pointer",
                backgroundImage: `url(${imageURLs[index]})`,
                backgroundSize: "cover",
                backgroundPosition: "center",
              }}
            >
              <input {...getInputProps()} />
              {imageURLs[index] && (
                <Box sx={{ position: "relative", top: 0, right: 0 }}>
                  <IconButton onClick={(e) => deleteImage(index, e)}>
                    <DeleteIcon />
                  </IconButton>
                </Box>
              )}
              <span>DÃ©posez des fichiers ici</span>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <ul>{files}</ul>
    </Box>
  );
}

export default MyDropzone;
