import { TextField } from "@mui/material";
import React, { useRef, useEffect, useState } from "react";

function Autocomplete({ id, newPost, setNewPost }) {
  const inputLocation = useRef(null);
  const [TemporaryAdresse, setTemporaryAdresse] = useState({});

  function AddAutoComplete() {
    const options = {
      componentRestrictions: { country: "fr" },
      fields: ["address_components", "geometry", "icon", "name"],
      strictBounds: false,
    };
    console.log(inputLocation.current);
    const autocomplete = new window.google.maps.places.Autocomplete(
      inputLocation.current,
      options
    );

    window.google.maps.event.addListener(
      autocomplete,
      "place_changed",
      function () {
        const autocompleteValue = autocomplete.getPlace();

        inputLocation.current.addEventListener("keyup", (e) => {
          if (e.keyCode == 13) {
            e.preventDefault();
            const adresse = {
              formatted_address: autocompleteValue?.name,
              street: autocompleteValue?.address_components[0]?.long_name,
              street_number:
                autocompleteValue?.address_components[0]?.long_name,
              route: autocompleteValue?.address_components[1]?.long_name,
              city: autocompleteValue?.address_components[2]?.long_name,
              administrative_area_level_1:
                autocompleteValue?.address_components[3]?.long_name,
              administrative_area_level_2:
                autocompleteValue?.address_components[4]?.long_name,
              country: autocompleteValue?.address_components[5]?.long_name,
              postal_code: autocompleteValue?.address_components[6]?.long_name,
            };

            setNewPost((newPost) => {
              return {
                ...newPost,
                adresse,
              };
            });
            setTemporaryAdresse(autocompleteValue);
          }
        });
      }
    );
  }

  useEffect(() => {
    window.initMap = AddAutoComplete;
    AddAutoComplete();
  }, [inputLocation.current]);

  // useEffect(() => {
  //   if (TemporaryAdresse?.address_components) {
  //     const adresse = {
  //       formatted_address: TemporaryAdresse?.name,
  //       street: TemporaryAdresse?.address_components[0]?.long_name,
  //       street_number: TemporaryAdresse?.address_components[0]?.long_name,
  //       route: TemporaryAdresse?.address_components[1]?.long_name,
  //       city: TemporaryAdresse?.address_components[2]?.long_name,
  //       administrative_area_level_1:
  //         TemporaryAdresse?.address_components[3]?.long_name,
  //       administrative_area_level_2:
  //         TemporaryAdresse?.address_components[4]?.long_name,
  //       country: TemporaryAdresse?.address_components[5]?.long_name,
  //       postal_code: TemporaryAdresse?.address_components[6]?.long_name,
  //     };

  //     setNewPost((newPost) => {
  //       return {
  //         ...newPost,
  //         adresse,
  //       };
  //     });
  //   }
  //   console.log("adresse => ", newPost);
  // }, [TemporaryAdresse]);

  return (
    <TextField
      label="Entrez votre adresse"
      variant="outlined"
      type="text"
      name="title"
      id={id}
      inputRef={inputLocation}
    />
  );
}

export default Autocomplete;
