import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

const PostComponent = () => {
  const [post, setPost] = useState({});
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:8000/api/posts/${id}`)
      .then((res) => res.json())
      .then((data) => setPost(data));
    console.log(id);
  }, []);

  return <h1>aaa</h1>;
};

export default PostComponent;
