import { TextField } from "@mui/material";
import React, { useRef, useEffect, useState } from "react";

function Autocomplete({ id }) {
  const inputLocation = useRef(null);
  const [TemporaryAdresse, setTemporaryAdresse] = useState({});
  const [adresse, setAdresse] = useState({});

  function AddAutoComplete() {
    const options = {
      componentRestrictions: { country: "fr" },
      fields: ["address_components", "geometry", "icon", "name"],
      strictBounds: false,
    };
    console.log(inputLocation.current);
    const autocomplete = new window.google.maps.places.Autocomplete(
      inputLocation.current,
      options
    );

    window.google.maps.event.addListener(
      autocomplete,
      "place_changed",
      function () {
        const autocompleteValue = autocomplete.getPlace();

        inputLocation.current.addEventListener("keydown", (e) => {
          if (e.keyCode == 13) {
            e.preventDefault();
            console.log("auto => ", autocompleteValue);
            setTemporaryAdresse(autocompleteValue);
          }
        });
      }
    );
  }
  useEffect(() => {
    window.initMap = AddAutoComplete;
    AddAutoComplete();
  }, [inputLocation]);
  useEffect(() => {
    console.log("adresse => ", adresse);
    console.log("TemporaryAdresse => ", TemporaryAdresse);

    if (TemporaryAdresse?.address_components) {
      const adresse = {
        street: TemporaryAdresse?.address_components[0]?.long_name,
        city: TemporaryAdresse?.address_components[1]?.long_name,
      };
      setAdresse(adresse);
    }
  }, [TemporaryAdresse]);

  return (
    <TextField
      label="Entrez votre adresse"
      variant="outlined"
      type="text"
      name="title"
      id={id}
      inputRef={inputLocation}
    />
  );
}

export default Autocomplete;
